delete from au_rule where resource_type = 'REQUEST_TASK' and resource_sub_type = 'AVIATION_AER_CORSIA_WAIT_FOR_AMENDS' and handler = 'requestTaskAccessHandler' and role_type = 'REGULATOR' and scope = 'REQUEST_TASK_EXECUTE';

changeSet.sql:
update au_rule set handler='requestAccountBasedAccessHandler' where handler='requestAccessHandler' and role_type='VERIFIER';
update au_rule set handler='requestActionAccountBasedViewHandler' where handler='requestActionViewHandler' and role_type='VERIFIER';
update au_rule set handler='requestTaskAccountBasedAccessHandler' where handler='requestTaskAccessHandler' and role_type='VERIFIER';
update au_rule set handler='requestTaskAssignAccountBasedAccessHandler' where handler='requestTaskAssignAccessHandler' and role_type='VERIFIER';

rollback.sql:
update au_rule set handler='requestAccessHandler' where handler='requestAccountBasedAccessHandler' and role_type='VERIFIER';
update au_rule set handler='requestActionViewHandler' where handler='requestActionAccountBasedViewHandler' and role_type='VERIFIER';
update au_rule set handler='requestTaskAccessHandler' where handler='requestTaskAccountBasedAccessHandler' and role_type='VERIFIER';
update au_rule set handler='requestTaskAssignAccessHandler' where handler='requestTaskAssignAccountBasedAccessHandler' and role_type='VERIFIER';

