changeSet.sql:
      	update request_sequence
      	set business_identifier = account_id::text;

      	insert into request_sequence(id, account_id, version, sequence, type, business_identifier)
      	values(nextval('request_sequence_seq'), 0, 0, nextval('request_sequence_seq'), 'SYSTEM_MESSAGE_NOTIFICATION', null);

      	alter table request_sequence
			drop column if exists account_id;

rollback.sql:
		alter table request_sequence
		add account_id bigint;

		update request_sequence
	    set account_id = NULLIF(business_identifier, '')::int;

        alter table request_sequence
        drop column if exists business_identifier;

        delete from request_sequence
        where type = 'SYSTEM_MESSAGE_NOTIFICATION';

